REPLACE INTO `re_item_desc_db` VALUES ('5259', 'Headgear', '', '', 'A hat made in the form of a big animal found in one oriental country.<br>Although it is very big,this animal is innocent and kind. It suck in and sprikle water by using its nose.<br>VIT +1<br>When attacking,has a low chance of autocasting Water Ball lvl 3.<br>Enable to use Aqua Benedicta.', '', '', '');
