REPLACE INTO `re_item_desc_db` VALUES ('5451', 'Costume Headgear', '', '', 'Gold Dragonhelm is specially constructed to offer the utmost in protection and offense against Dragon Type Monsters.<br>The Gold Dragonhelm is the epitome of Dragonhelm technology, making it a wonderful showpiece, as such it is a Costume item.<br>Available by quest until February 7, 2012 in celebration of the Year of the Dragon.<br>12% tolerance to Dragon Race monsters<br>8% damage to Dragon Race monsters<br>5% bonus XP when fighting Dragons.<br><span style=\'color:#770000\'>Cannot be upgraded<br>Account Bound</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('5452', 'Headgear', '', '', 'Silver Dragonhelm is specially constructed to offer the utmost in protection and offense against Dragon Type Monsters.<br>The Silver Dragonhelm may be upgraded by Dragos in Prontera using special ingredients for higher success.<br>Available by quest until February 7, 2012 in celebration of the Year of the Dragon.<br>4% tolerance to Dragon Race monsters<br>+1% tolerance to Dragon Race monsters per refine level, up to a limit of +8.<br>+1% damage to Dragon Race monsters up to a limit of +8<br><span style=\'color:#770000\'>Cannot be upgraded<br>Account Bound</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('5453', 'Headgear', '', '', 'Copper Dragonhelm is specially constructed to offer the utmost in protection against Dragon Type Monsters.<br>The materials used in creating the Copper Dragonhelm work perfectly when used as components to safely upgrade either the Copper or Silver Dragonhelms, talk to Dragos in Prontera for details.<br>Available by quest until February 7, 2012 in celebration of the Year of the Dragon.<br>+1% tolerance to Dragon Race monsters per refine level, up to a limit of 8%.<br><span style=\'color:#770000\'>Cannot be upgraded<br>Account Bound</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('5454', 'Headgear', '', '', 'A light brown puppy dog. It looks quite cute sitting on your head.<br>STR +1, INT +1<br>Adds a chance 100% CRIT for 3 seconds when receive physical attack.<br>Adds a chance 100% ignore magical defense for 3 seconds when receive magical attack.', '', '', '');
