REPLACE INTO `re_item_desc_db` VALUES ('5877', 'Headgear', '', '', 'These colors don\'t run, but you will (faster) when wearing this Old Glory adorned hat. <br>Increase Movement when equipped.<br>If upgraded to +7 or higher, FLEE +10, LUK +10.<br><span style=\'color:#ff0000\'>Account Bound</span>', '', '', '');
