REPLACE INTO `re_item_desc_db` VALUES ('6285', '', '', '', 'This is the manual book for producing Enriched White PotionZ, vitata500, Enriched cell juice.<br>Enriched White PotionZ: <span style=\'color:blue\'>Empty Test Tube 10	+ white potion 20	+ white herb 10	+ Alcohol 1 </span><br>vitata 500: <span style=\'color:blue\'>Empty Test Tube 10	+ grape 10 + honey 10 + Blue Herb 10</span><br>Concentrated Juice: <span style=\'color:blue\'>Empty Test Tube 10 + Awakening Potion 5 + Intensive Potion 5 + Hot Sauce 5</span><br>Cure Landscape: <span style=\'color:blue\'>Empty Test Tube 10 + elixir 5 + Green Herbs 20 + Mastela Fruit 1 + Yggdrasil Leaf 1</span>', '', '', '');
