REPLACE INTO `re_item_desc_db` VALUES ('12719', '', '', '', 'An ash powder that causes the Oblivion state.<br>Oblivion lasts for 300 seconds. If under Oblivion state, SP doesn\'t recover naturally and cannot be canceled by Lex Divine or Green Potion.<br>You cannot use skills while caught by Oblivion ashes.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('12720', '', '', '', 'A poison that pollutes a wound.<br>When a target is caught by this poison for 300 seconds, recovery skills decrease in effect by 20%.<br>The poison is extra potent if also inflicted by fatal wound.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('12721', '', '', '', 'A poison that numbs nerves.<br>It continues for 300 seconds, and generates damage every 3 seconds. This ignores the weapons or card options which don\'t disconnect spell.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('12722', '', '', '', 'A poison that causes a feverish state.<br>For 300 seconds, it spells darkness that can\'t be removed and causes hallucination. Gets 100 damages for one second, and while getting damages if casting was ongoing, the casting also can be cancelled.', '', '', '');
