REPLACE INTO `re_item_desc_db` VALUES ('15031', 'Armor', '', '', 'Armor that is only provided for members of the Eden Group.<br>There are various sizes so tall people don\'t have to worry about it.<br>MaxHP +500, MaxSP	+50,<br>MDEF +5.<br>Cannot be upgraded.', '', '', '');
