REPLACE INTO `re_item_desc_db` VALUES ('15047', 'Armor', '', '', '*Stats during WoE & PVP<br>MDEF +10.<br>Reduces damage taken from enemy players by 2%.<br>Refined to +6 or higher, Perfect Dodge +5, reduces ranged damage taken by enemy players by 20%.<br>Refined to +9 or higher, MaxHP +15%<br>[<span style=\'color:#6A5ACD\'>WoE set</span>]<br><span style=\'color:#008800\'>WoE Boots</span><br><span style=\'color:#008800\'>WoE Manteau</span><br><span style=\'color:#008800\'>WoE Suits</span><br>DEX +5<br>Reduces damage from players by 15%', '', '', '');
