REPLACE INTO `re_item_desc_db` VALUES ('15093', 'Armor', '', '', 'MDEF +5<br>MaxHP, MaxSP +5%.<br>ATK +10, MATK +10, HIT +10, FLEE +10.<br>Banishing Point damage is increased by 10%.<br>Hundred Spears damage is increased by 10%.<br>Low chance to cast Level 10 Heal and Level 10 Stormgust when physically attacking.<br>The chance is increased with a higher upgrade level.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('15094', 'Armor', '', '', 'MaxHP, MaxSP +5%.<br>MATK +10, FLEE +10.<br>Variable casting time of Lord of Vermillion, Meteor Storm, Heavens Drive and Storm Gust is reduced by 1 second.<br>If the Upgrade Level is an even number, the cast cannot be interrupted and the variable casting time is increased by 20%.<br>If the Upgrade Level is an odd number, MDEF +1 and the variable casting time is reduced by 1% for every 2 refine level.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('15095', 'Armor', '', '', 'MaxHP, MaxSP +5%.<br>ATK +10, MATK +10, HIT +10, FLEE +10.<br>Dragon Combo, Sky Blow, Earth Shaker and Fallen Empire damage is increased by 10%.<br>Judex and Adoramus damage is increased by 10%.<br>Small chance to cast Lex Aeterna Level 1 when physically attacking.<br>Small chance to cast Lex Kyrie Eleison Level 10 when magically attacking.<br>The chance is increased with a higher upgrade level.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('15096', 'Armor', '', '', 'MDEF +5<br>MaxHP, MaxSP +5%, ATK +10, MATK +10.<br>[<span style=\'color:#6A5ACD\'>Base STR 90</span>]<br>Str +1 every 2 refine level.<br>[<span style=\'color:#6A5ACD\'>Base INT 90</span>]<br>Int +1 every 2 refine level.<br>[<span style=\'color:#6A5ACD\'>Base VIT 90</span>]<br>Vit +1 every 2 refine level.<br>[<span style=\'color:#6A5ACD\'>Base AGI 90</span>]<br>Agi +1 every 2 refine level.<br>[<span style=\'color:#6A5ACD\'>Base DEX 90</span>]<br>Dex +1 every 2 refine level.<br>[<span style=\'color:#6A5ACD\'>Base LUK 90</span>]<br>Luk +1 every 2 refine level.<br>Has a chance to inflict Stun, Poison, Silence, Curse status when doing physical attack.<br>A chance is increased depend on refine level.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('15097', 'Armor', '', '', 'MaxHP +5%, MaxSP +5%<br>Increase damage of Fatal Manace, Triangle Shot, Cross Impact and Cross Ripper Slasher by 10%<br>Has a chance to get Crit +20, Flee +20, ATK +10% and MATK +10% bonus for 10 seconds when doing physical attack.<br>A chance is increased depend on refine level.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('15098', 'Armor', '', '', 'MaxHP +5%, MaxSP +5%<br>Aspd +1<br>All Stats +1<br>Increase ranged physical damage by 3%,<br>Has a chance to cast Improve Concentration Lv.10 or Double Strafe Lv.10 when doing physical attack.<br>Chance is increased depend on refine level.', '', '', '');
