REPLACE INTO `re_item_desc_db` VALUES ('15146', 'Armor', '', '', 'A clothes that was made for no purpose.<br>Enable use of level 1 Endure. (Not implemented)<br>MATK +50<br>If wearer\'s base level 120 or higher, increase MATK +50<br>If wearer\'s base level 140 or higher, additional MATK +50<br>When equipped with <span style=\'color:#008800\'>Cape of Ancient Lord</span>,<br>MaxSP +10%, every 1 refine of armor increase MaxSP +1%.<br>FLEE +10, every 1 refine of armor increase FLEE +1.<br>When equipped with <span style=\'color:#008800\'>Cloak Of Survival</span>,<br>MaxHP +10%, every 1 refine of armor increase MaxHP +1%.<br>MATK +2%, every 1 refine of armor increase MATK +1%.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('15147', 'Armor', '', '', 'A clothes that will deal serious damage to people\'s spirit when talking.<br>Enable use of Level 1 Improve Concentration.<br>Every 1 refine, ignore 4% physical defense of Demi-human, Demon and undead monster.<br>When equipped with <span style=\'color:#008800\'>Morrigane\'s Manteau</span>,<br>ignore 15% physical defense of Demi-human, Demon and undead monster.<br>LUK +3, every 1 refine of armor increase LUK +1.<br>CRIT +10, every 1 refine of armor increase CRIT +1.<br>When equipped with <span style=\'color:#008800\'>Valkyrie Manteau</span>,<br>ignore 15% physical defense of Demi-human, Demon and undead monster.<br>MaxHP +10%, every 1 refine of armor increase MaxHP +1%<br>ATK +2%, every 1 refine of armor increase ATK +1%.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16000', 'Mace', '', '', 'This mace\'s shape is inspired by a Snake, which symbolizes the fountain of knowledge.<br>MaxSP +50.<br>The attack power of \'Acid Terror\' and \'Bomb\' increases by 20%.<br>Every potion\'s recovery amount increases by 10%.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16001', 'Mace', '', '', 'The official bag of the Alchemist Society. Its unique design and bright red color is pupular among women.<br>MaxHP +200.<br>The attack power of \'Acid Terror\' and \'Bomb\' increases by 20%.<br>If Str is over 90, has a 5% chance of adding Stun with close-ranged physical attacks.<br>When a monster is killed, it has a chance of droping a random potion.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16002', 'Mace', '', '', '<span style=\'color:blue\'>Rental Items</span><br>A huge, unwieldy club that can knock enemies unconscious.<br>Causes <span style=\'color:#880000\'>Stun</span> effect to enemies by 10% chance.<br>Increases damage against small, medium, large size monsters by 40%.', '', '', '');
