REPLACE INTO `re_item_desc_db` VALUES ('16419', '', '', '', 'A box containing 30 Greed Scrolls, which each enable a single cast of Greed.<br>This scroll has the same restrictions as the normal casting of Greed; some maps do not allow Greed to be used.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16420', '', '', '', 'A pack of consumables to<br>aid the adventurer on the<br>long journey.<br><br>Contains:<br>10 Battle Manual<br>10 Bubble Gum<br>20 Lv 10 Agi Scroll<br>20 Lv 10 Blessing Scroll<br>10 Token Of Siegfried<br>10 Life Insurance<br>5 Kafra Card<br>10 Stew of Immortality<br>10 Hwegelmir\'s Tonic<br>10 Steamed Scorpion<br>10 Cooked Nine Tail\'s Tails<br>10 Steamed Tongue<br>10 Dragon Breath Cocktail<br><br>Adventurer Pack can not be<br>traded, dropped, or sold;<br>it can be stored.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16421', '', '', '', 'A box that contains 1 Written Oath of Marriage, to be used on your wedding day.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16422', 'Headgear', '', '', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Baphomet Horns</span><br>Duration: 7 Days after opening.<br>A headgear made from the horns of Baphomet.<br>Inflicts 12% more damage to Demihumans.<br>Increases ATK based on the wearer\'s Job Lvl.<br>This item cannot be Dropped or Traded.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16423', '', '', '', '<span style=\'color:#ff0000\'>This package is tradeable.</span><br><span style=\'color:#ff0000\'>The items inside are not.</span><br><br>Contains:<br>Episode 13.1 Poporing Key<br>2 Battle Manuals<br>10 Yellow Butterfly Wings<br><br>The Episode 13.1 Poporing Key will disappear on <span style=\'color:#ff0000\'>07/09/09</span> during maintenance;<br>at that time, the Episode 13.1 content will be fully available to everyone on Valkyrie.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16424', 'Two-handed Sword', '', 'Shadow', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Executioner</span><br>Duration: 7 Days after opening.<br>A gruesome sword used<br>to behead criminals judged with the death penalty.<br>Pierces Defense of<br>Demihuman monsters.<br>Increases damage inflicted on Demihuman monsters by 20%, but increases damage received by Demihuman monsters by 10%.<br>This item cannot be Dropped or Traded.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16425', 'Sword', '', '', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Cutlas</span><br>Duration: 7 Days after opening.<br>A one-handed sword<br>popularized by its use<br>by sea adventurers.<br>Enables use of<br><span style=\'color:#008800\'>Level 5 Bash</span>.<br>STR +2<br>DEF +1<br>This item cannot be Dropped or Traded.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16426', 'Dagger', '', '', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Moonlight Dagger</span><br>Duration: 7 Days after opening.<br>A dagger imbued with<br>lunar spirit and the power to drain an enemy\'s soul.<br>Regain 3 SP<br>with each attack.<br>Max SP +10%<br>This item cannot be Dropped or Traded.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16427', 'Mace', '', '', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Wrench</span><br>Duration: 7 Days after opening.<br>A heavy tool with many practical applications.<br>Enable a chance of<br>randomly inflicting the <span style=\'color:#880000\'>Stun</span>, <span style=\'color:#880000\'>Blind</span>, <span style=\'color:purple\'><b>Poison</b></span><br>or <span style=\'color:#880000\'>Frozen</span> statuses with<br>each attack.<br>This item cannot be Dropped or Traded.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16428', 'One-handed Sword', '', 'Fire', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Solar Sword</span><br>Duration: 7 Days after opening.<br>A one-handed sword<br>adorned with an engraving of the sun, symbolizing battle spirit and vigor.<br>Regains 1% of the damage inflicted on its target as HP with each attack.<br>Drains 15 SP from its<br>owner every 10 seconds.<br>This item cannot be Dropped or Traded.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16429', 'Axe', '', 'Wind', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Tomahawk</span><br>Duration: 7 Days after opening.<br>A light axe imbued with the <span style=\'color:#7BCC70\'>Wind</span> property which can be hurled at enemies.<br>Enables use of<br><span style=\'color:#008800\'>Tomahawk Throwing</span>.<br>This item cannot be Dropped or Traded.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16430', 'Bow', '', 'Holy', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Rudra Bow</span><br>Duration: 7 Days after opening.<br>A bow imbued with the<br>sacred essense of Rudra, one of the ancient gods.<br>Adds 50% tolerance to the Poison, Curse, Silence,<br>Chaos and Blind statuses. Enables the use of<br><span style=\'color:#008800\'>Level 1 Heal</span> and<br><span style=\'color:#008800\'>Level 1 Cure</span>. INT +5<br>This item cannot be Dropped or Traded.', '', '', '');
