REPLACE INTO `re_item_desc_db` VALUES ('16972', '', '', '', 'A box that contains 4 costume headgears that convey different patterns of the weather.<br><span style=\'color:#ff0000\'>This box can be stored and traded, the items inside are account bound.</span><br>Opening it will give all 4 of the following:<br>-1x Costume: Shining Sun<br>-1x Costume: Rainbow<br>-1x Costume: Lightning Cloud<br>-1x Costume: Rain Cloud<br><span style=\'color:#ff0000\'>Once this item opened, it cannot be refunded.</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16973', 'Costume', '', '', 'A box that contains<br>1 Costume-Yellow Hat<br>Description:<br>A simple yellow hat with a long feather in it. Fashionistas wear it to the side lest they be shunned.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16974', '', '', '', 'A box that contains 2 costume headgears that give you the appearance of a famous old-timey actor.<br><span style=\'color:#ff0000\'>This box can be stored and traded, the items inside are account bound.</span><br>Opening it will give both of the following:<br>-1x Costume: Old Timey Mustache<br>-1x Costume: Old Timey Bowler<br><span style=\'color:#ff0000\'>Once this item opened, it cannot be refunded.</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16975', 'Costume', '', '', 'A box that contains<br>1 Costume-Singing Bird<br>Description:<br>A newly hatched baby bird chirps it\'s tunes on a twig of dreams.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16976', '', '', '', 'Wearing these items will give you the presence of the tastiest poultry in all of Rune Midgard.<br>A box that contains 1 of each:<br>-1x Costume: Chicken Beak<br>-1x Costume: Roosters Comb<br>This box can be stored and traded, the items inside are account bound.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16977', 'Costume', '', '', 'A box that contains<br>1 Costume-Mini Crown<br>Description:<br>A mini red crown affixed to a headband and worn off to the side. Though not a true symbol of royalty, this crown does have a regal air about it.', '', '', '');
