REPLACE INTO `re_item_desc_db` VALUES ('17104', '', '', '', 'A box that contains 50 HD Oridecon<br>Description:<br>High Density Oridecon<br>that has been buried<br>deep underground.<br>Used as a material to<br>strengthen weapons currently from refine levels 7 through 9.<br>This item\'s density ensures that refine attempts will not break the weapon and if the attempt fails it will only downgrade by 1.<br>Only a Blacksmith named Mighty Hammer in Payon can use this ore to refine.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('17105', '', '', '', 'A box that contains 50 HD Elunium<br>Description:<br>High Density Elunium<br>that has been buried<br>deep underground.<br>Used as a material<br>to strengthen armor currently from refine levels 7 through 9.<br>This item\'s density ensures that refine attempts will not break the armor and if the attempt fails it will only downgrade by 1.<br>Only a Blacksmith named Mighty Hammer in Payon can use this ore to refine.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('17106', '', '', '', 'A box that contains 10 Gym Passes<br>Description:<br>A Gym Pass that can be<br>given to a special<br>NPC to learn a skill<br>that will increase your<span style=\'color:#FFFFFF\'>	 </span> Maximum Weight<br>Capacity by 200 for<br>each skill level.<br>This special skill<br>can only be learned<br>up to Level 10.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('17107', '', '', '', 'A special scroll to celebrate the month of Gemini. You can get a valuable item such as Gemini Crown, Dark Snake Lord Card, Elven Ears, Gentleman\'s Pipe, Hawk Eyes etc.', '', '', '');
