REPLACE INTO `re_item_desc_db` VALUES ('17109', '', '', '', 'May cause unrestrained delight from the receiver of its contents, Each May You Groove On Pack contains:<br><span style=\'color:#ff0000\'>For every 10 May You Groove On Packs purchased 1 Safe to 10 Certificate will be added to the purchasing account.</span><br>-1 Safe to 7 Chance Box<br>And randomly:<br>-1x Battle Manual X3<br>-1x Very Soft Plant (Leaf Cat tame item)<br>-1x Bloody Branch<br>-1x Note Headphone [1] (headgear)<br>-1x Gemini Crown (headgear)<br>-1x Gemini Diadem (headgear)<br>-1x Observer (headgear)', '', '', '');
