REPLACE INTO `re_item_desc_db` VALUES ('17124', 'Headgear', '', '', '<span style=\'color:#ff0000\'>This box cannot be traded with others.</span><br>A box containing one Cat Ear Beret<br>A cute beret with cat ears! Cats don\'t normally wear hats but if they did they would wear this one.<br>If upgraded to +5 or higher, increases 2% of Physical damage on Demi-human monsters and reduces 2% of physical damage from Demi-human monsters per refine level over 4 and caps at level 12.<br><span style=\'color:#ff0000\'>Once you open the box, it cannot be refunded.</span>', '', '', '');
