REPLACE INTO `item_desc_db` VALUES ('1629', 'Rod', '', 'Neutral', 'A staff once used by a very gentle man somewhere a long ago.<br>A very narrow sword is hidden inside the staff.<br>MATK + 125, DEX + 1<br>[<span style=\'color:blue\'>+ Magician Hat</span>]<br>DEX + 2, INT + 2, SP Recovery + 5%. Each refine increases MATK by 1%.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1630', 'Rod', '', 'Neutral', 'A staff made of Crystal. It shines beautifully by the light.<br>This staff also can help the holder recover from damage.<br>MATK + 125, INT + 3<br>Increased recovery skill effect + 5%.<br>When performing a Magical attack, there\'s a small chance recover SP and HP.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1631', 'Rod', '', 'Neutral', 'A holy stick which is made of Rune of Odin, speciallized for exorcism.<br>MATK + 140, Reduce Casting Time of <span style=\'color:#008800\'>Holy Light, Turn Undead, and Magnus Exorcismus</span> by 25%.<br>[<span style=\'color:blue\'>Sacred Scripture & Holy Stick Equip Set</span>]', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1632', 'Rod', '', '', 'A Rod that has been modified for brave magicians who face enemies in battle.<br>It is especially designed for battles against Demi-Humans.<br>MATK + 125, INT + 4, DEX + 3<br>Ignores Magic Defense from Demi-Human monsters by 25%.<br>Enables a 5% chance to inflict the Stun effect when using magical attacks.<br>Indestructible.<br>Attack Strength : <span style=\'color:#777777\'>0</span>', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1633', 'Rod', '', '', 'A Rod that has been modified for brave magicians who face enemies in battle.<br>It is especially designed for battles against Demi-Humans.<br>MATK + 125, INT + 3, DEX + 3<br>Increases Magic Damage against Demi-Human monsters by 15%.<br>Enables a 5% chance to inflict the Stun effect when using magical attacks.<br>Indestructible.<br>Attack Strength : <span style=\'color:#777777\'>0</span>', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1634', 'Rod', '', '', 'A Rod that is intended to heal people during battle.<br>It provides special treatment to serious wounds.<br>MATK + 125<br>Increases the Recovery Rate of Heal, Sanctuary by 14% when they are cast by the wearer.<br>Restores 5 SP every 10 sec.<br>Indestructible.<br>Attack Strength : <span style=\'color:#777777\'>0</span>', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1635', 'Rod', '', '', 'A Rod that is intended to heal people during battle.<br>It has been optimized to heal more rapidly.<br>MATK + 125, INT + 3, DEX + 2<br>Reduces Cast Delay by 15%.<br>Restores 5 SP every 10 sec.<br>Indestructible.<br>Attack Strength : <span style=\'color:#777777\'>0</span>', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1636', 'Two-handed Staff', '', '', 'A strong magical staff covered by sharp thorned vines.<br>MATK + 160, INT + 3, DEX + 3<br>Each refine adds magic defense bypassing to your magic attacks.<br>Each refine reduces the aftercast delay of your skills.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1637', 'Rod', '', '', 'A magical rod that enables you to keep draining the enemies mentality.<br>MATK + 170, INT + 3, DEX + 2<br>Recovery SP + 8%<br>There is a small chance while magically attacking, on a radius 11 * 11 of your cell and drain every enemies SP by 20%.<br>If Refine rate is 10, the area becomes 19 * 19 and you drain every enemies SP by 60%.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1638', 'Rod', '', '', 'A staff made with a branch from the tree Yggdrasil, created with the sole purpose to heal. The staff has something inscribed in ancient language, saying "Mubeungjangsugiwon", or "Forever free of disease, and have a long life".<br>MATK + 125<br>Increases 15% chance to yourself by <span style=\'color:#008800\'>Heal and Sanctuary</span> skill.', '', '', '');
