REPLACE INTO `item_desc_db` VALUES ('1640', 'Rod', '', '', 'MATK+ 135<br>Increases magical attack against Demi-Human monster by 15%.<br>Adds 25% magical defense bypassing on Demi-Human monster.<br>Indestructible (except in upgrade attempts).<br>Each refine increases MATK + 1%.<br>[<span style=\'color:#333399\'>Refine Rate 6~10</span>]<br>Increases magic defense bypassing by 5%.<br>[<span style=\'color:#333399\'>Refine Rate 9~10</span>]<br>Increases MATK + 5%.<br>Decreases cast time by 5%.<br>Decreases aftercast delay by 5%.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1641', 'Rod', '', '', 'MATK + 135<br>Increases the effectiveness of your Heal and Sanctuary skills by 14%.<br>Skill Aftercast Delay - 10%.<br>Indestructible (except in upgrade attempts).<br>[<span style=\'color:#333399\'>Refine Rate 6~10</span>]<br>Increases the effectiveness of your Heal by 5%.<br>Enables use of <span style=\'color:#008800\'>Level 1 Blessing</span>.<br>[<span style=\'color:#333399\'>Refine Rate 9~10</span>]<br>Add a chance of auto casting <span style=\'color:#008800\'>Level 10 Heal</span> when using skill Heal.<br>[<span style=\'color:#333399\'>Refine Rate 10</span>]<br>Increases the effectiveness of your Heal by 10%.', '', '', '');
