REPLACE INTO `item_desc_db` VALUES ('1643', 'Rod', '', 'Neutral', 'A staff made from the wood of a tree long exposed to magic power. The staff holds vast magic power, and at times almost looks like an old vagrant.<br>INT + 4. MATK + 155<br>INT bonus increases if refined past + 6, at the cost of reduced Max HP and Max SP.<br>[<span style=\'color:#333399\'>Refine Rate 6</span>]<br>INT + 1, Max HP - 200, Max SP - 100.<br>[<span style=\'color:#333399\'>Refine Rate 7</span>]<br>INT + 2, Max HP - 400, Max SP - 200.<br>[<span style=\'color:#333399\'>Refine Rate 8</span>]<br>INT + 3, Max HP - 600, Max SP - 300.<br>[<span style=\'color:#333399\'>Refine Rate 9</span>]<br>INT + 4, Max HP - 800, Max SP - 400.<br>[<span style=\'color:#333399\'>Refine Rate 10</span>]<br>INT + 5, Max HP - 1000, Max SP - 500.', '', '', '');
