REPLACE INTO `item_desc_db` VALUES ('1975', 'Whip', '', '', 'The adventurers who are brave the item which is provided from the hazard adventurer guild.<br>Increases damage by 50%.<br>Impossible to refine this item.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1976', 'Whip', '', '', 'A whip with awesome power. Don\'t even go there with your imagination!<br>Inflict 10% more damage with Throw Arrow and Arrow Vulcan.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1977', 'Whip', '', '', 'An especially made whip for battling against Demi-Humans; this whip is strong enough to cut their flesh.<br>DEX + 2<br>Increases Physical Attack Strength towards Demi-Human monsters by 95%.<br>Ignores Physical Defense from Demi-Human monsters by 20%.<br>Indestructible.<br>Attack Strength : <span style=\'color:#777777\'>50</span>', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1978', 'Whip', '', '', 'An especially made whip for battling against Demi-Humans; this whip is strong enough to cut their flesh.<br>DEX + 2<br>Increases Physical Attack Strength towards Demi-Human monsters by 95%.<br>Increase Arrow Vulcan Damage by 20%.<br>Indestructible.<br>Attack Strength : <span style=\'color:#777777\'>50</span>', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1979', 'Whip', '', '', 'The strong tentacles of Nepentis brings forth, a hard yet soft rope.<br>INT + 2<br>Add small chance to <span style=\'color:#880000\'>Frozen</span> on the enemy when using <span style=\'color:#008800\'>Throw Arrow</span>.<br>If Refining level is over 9, the probability of inflinting Freeze gets higher.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1980', 'Whip', '', '', 'At the end of the rope is a power that enables enemies to back off.<br>The users hit by this rope have nothing they can do but back off by its powers.<br>AGI + 2, DEX + 3<br>The <span style=\'color:#008800\'>Throw Arrow</span> skill shoves the enemy 2 cells back.<br>The <span style=\'color:#008800\'>Arrow Vulcan</span> skill shoves the enemy 3 cells back.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1981', 'Whip', '', '', 'Increases physical attack against Demi-Human monster by 95%.<br>Adds 20% defense bypassing on Demi-Human monster.<br>Indestructible (except in upgrade attempts).<br>[<span style=\'color:#333399\'>Refine Rate 6~10</span>]<br>Adds 5% defense bypassing on Demi-Human monster.<br><span style=\'color:#ff0000\'>[Slaughter]</span> Lv 1<br>[<span style=\'color:#333399\'>Refine Rate 9~10</span>]<br>Add a chance of auto casting <span style=\'color:#008800\'>Level 5 Tarot Card</span> on the monster when using skill Arrow Vulcan.', '', '', '');
