REPLACE INTO `item_desc_db` VALUES ('1983', 'Whip', '', '', '<span style=\'color:blue\'>Rental Items</span><br>A whip with a spiked ball attached at the end.<br>Increases damage against small, medium, large size monsters by 40%.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1984', 'Whip', '', '', 'A whip crafted from the flexible stem of a plant.<br>Decreases the variable casting time of <span style=\'color:#008800\'>Deep Sleep Lullaby</span> and <span style=\'color:#008800\'>Swing Dance</span> by 2 seconds.<br>For each refine above + 6, decreases the SP cost of those skills by 4 sp.', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('1985', 'Whip', '', '', 'A whip created from a magically enchanted rose that radiates the smell of roses.<br>When dealing physical damage there is a chance to trigger <span style=\'color:#008800\'>Level 1 Voice of Siren</span>.<br>INT + 2, AGI -2, MATK + 130.', '', '', '');
