REPLACE INTO `item_desc_db` VALUES ('5613', 'Headgear', '', '', 'A headgear that looks like flapping angel wings.<br>It makes you feel might lighter when you wear it, though it also makes you feel pretty dizzy.<br>ASPD and Casting Speed + 3%<br>INT + 1.<br>Cannot be traded or dropped, but can be placed in Kafra Storage.', '', '', '');
