REPLACE INTO `item_desc_db` VALUES ('5622', 'Headgear', '', '', 'This hat makes the wearer feel as if he is floating on air.<br>AGI + 2, DEX + 1,<br>Dodge +10.<br>This headgear\'s upgrade level affects the percentage of Cast Time reduction.<br>Cannot be Dropped, or Tradable<br>Can be placed in Kafra Storage', '', '', '');
