REPLACE INTO `item_desc_db` VALUES ('5624', 'Headgear', '', '', 'A must-have item for those that care about appearances:<br>it can increase your beauty threefold.<br>MDEF + 5.<br>Impossible to Upgrade.<br>Cannot be Dropped, or Tradable<br>Can be placed in Kafra Storage', '', '', '');
