REPLACE INTO `item_desc_db` VALUES ('16410', 'Headgear', '', '', 'A box that contains 1 Chung Hairpin in it.<br><br>If you wear this, it feels like you become a fancy Chung aristocrat.<br>LUK + 3, MDEF +4, Def+5<br>When receiving any physical attack, Mammonite Lv5 will be activated at a low rate.<br>(If you have no zeny, it won\'t be activated.)', '', '', '');
REPLACE INTO `item_desc_db` VALUES ('16412', 'Headgear', '', '', 'A box that contains 1 Ice Ear Wing in it.<br><br>Earrings made of ice and other special chemicals. If you wear these, you can beat off the heat by its cool energy!<br>LUK + 1.<br>When receiving any physical attack, Autospell Coldbolt Lv5 will be activated at a low rate.', '', '', '');
