REPLACE INTO `re_item_desc_db` VALUES ('2420', 'Footwear', '', '', 'Heavenly sandals worn<br>by the Guardian Angel<br>of Super Novices.<br>Max HP + 100.<br><span style=\'color:#6A5ACD\'>Angel\'s Protection Set</span><br><span style=\'color:#3CB371\'>Angel\'s Kiss</span><br><span style=\'color:#3CB371\'>Angelic Protection</span><br><span style=\'color:#3CB371\'>Angelic Cardigan</span><br><span style=\'color:#3CB371\'>Angel\'s Reincarnation</span><br><span style=\'color:#3CB371\'>Angelic Guard</span><br>HP +900<br>SP +100<br>A low Chance of casting<br>level 1 Assumptio<br>when attacked physically.', '', '', '');
