REPLACE INTO `re_item_desc_db` VALUES ('5593', 'Headgear', '', '', 'A bonnet made to resemble a rabbit. Despite it\'s cute appearance this hat gives it\'s wearer attack strength.<br>Attack and Magic Attack power increase by 10.<br>Adds 3% tolerance to Demihuman monsters.<br>After cast delay reduced by 3%.<br>If refined to +7 or higher, reduces damage from all elemental properties by 5%.', '', '', '');
