REPLACE INTO `re_item_desc_db` VALUES ('12719', '', '', '', 'An ash powder that causes the Oblivion state.<br>Oblivion lasts for 300 seconds. If under Oblivion state, SP doesn\'t recover naturally and cannot be canceled by Lex Divine or Green Potion.<br>You cannot use skills while caught by Oblivion ashes. ', '', '', '');
