REPLACE INTO `re_item_desc_db` VALUES ('12722', '', '', '', 'A poison that causes a feverish state.<br>For 300 seconds, it spells darkness that can\'t be removed and causes hallucination. Gets 100 damages for one second, and while getting damages if casting was ongoing, the casting also can be cancelled. ', '', '', '');
