REPLACE INTO `re_item_desc_db` VALUES ('12725', '', '', '', 'Casts \'Refresh\' removing all abnormal states and debuffs and prevents any abnormal state or debuff effects for 30 seconds.<br>Recover 25% HP and can be used while stunned, frozen or deep sleep.<br>Must wait 5 minutes to reuse. <br>Maximum carrying amount :10', '', '', '');
