REPLACE INTO `re_item_desc_db` VALUES ('16414', 'Headgear', '', '', 'A Box that contains 1 Turtle Hat.<br>Once opened, the Turtle Hat is account bound.<br><br>Description:<br>A disgruntled snapping turtle sits above your head. Count your fingers after putting this on.<br>Agi +1.<br>Has a low chance of casting Lv. 3 Decrease Agi on your enemy while being physically attacked.<br>Account Bound.<br><span style=\'color:#FF0000\'>Event Stats from 7/8 - 8/5:<br>Def +3,<br>Increases damage to turtle monsters on Turtle Island by 20%.<br>Decreases damage from turtle monsters on Turtle Island by 20%.<br>If upgraded to +7 or higher, Adds 25% EXP bonus for any monster killed.</span>', '', '', '');
