REPLACE INTO `re_item_desc_db` VALUES ('16419', '', '', '', 'A box containing 30 Greed Scrolls, which each enable a single cast of Greed.<br>This scroll has the same restrictions as the normal casting of Greed; some maps do not allow Greed to be used.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16420', '', '', '', 'A pack of consumables to<br>aid the adventurer on the <br>long journey.<br><br>Contains:<br>10 Battle Manual<br>10 Bubble Gum<br>20 Lv 10 Agi Scroll<br>20 Lv 10 Blessing Scroll<br>10 Token Of Siegfried<br>10 Life Insurance<br>5 Kafra Card<br>10 Stew of Immortality<br>10 Hwegelmir\'s Tonic<br>10 Steamed Scorpion<br>10 Cooked Nine Tail\'s Tails<br>10 Steamed Tongue<br>10 Dragon Breath Cocktail<br><br>Adventurer Pack can not be<br>traded, dropped, or sold;<br>it can be stored.', '', '', '');
