REPLACE INTO `re_item_desc_db` VALUES ('16422', '', '', '', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Baphomet Horns</span><br>Duration: 7 Days after opening.<br>A headgear made from the horns of Baphomet.<br>Inflicts 12% more damage to Demihumans.<br>Increases ATK based on the wearer\'s Job Lvl.<br>This item cannot be Dropped or Traded.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16423', '', '', '', '<span style=\'color:#ff0000\'>This package is tradeable.</span><br><span style=\'color:#ff0000\'>The items inside are not.</span><br><br>Contains: <br>Episode 13.1 Poporing Key<br>2 Battle Manuals<br>10 Yellow Butterfly Wings<br><br>The Episode 13.1 Poporing Key will disappear on <span style=\'color:#ff0000\'>07/09/09</span> during maintenance;<br>at that time, the Episode 13.1 content will be fully available to everyone on Valkyrie.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16424', '', '', 'Shadow', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Executioner</span><br>Duration: 7 Days after opening.<br>A gruesome sword used<br>to behead criminals judged with the death penalty.<br>Pierces Defense of<br>Demihuman monsters.<br>Increases damage inflicted on Demihuman monsters by 20%, but increases damage received by Demihuman monsters by 10%.<br>This item cannot be Dropped or Traded.<br><span style=\'color:#777777\'>Two-handed Sword</span><br>Attack Strength : <span style=\'color:#777777\'>190</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16425', 'Sword', '', '', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Cutlas</span><br>Duration: 7 Days after opening.<br>A one-handed sword<br>popularized by its use<br>by sea adventurers.<br>Enables use of<br><span style=\'color:#008800\'>Level 5 Bash</span>.<br>Str + 2<br>DEF + 1<br>This item cannot be Dropped or Traded.<br>Attack Strength : <span style=\'color:#777777\'>185</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16426', 'Dagger', '', '', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Moonlight Dagger</span><br>Duration: 7 Days after opening.<br>A dagger imbued with<br>lunar spirit and the power to drain an enemy\'s soul.<br>Regain 3 SP<br>with each attack.<br>Max SP +10%<br>This item cannot be Dropped or Traded.<br>Attack Strength : <span style=\'color:#777777\'>85</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16427', 'Mace', '', '', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Wrench</span><br>Duration: 7 Days after opening.<br>A heavy tool with many practical applications.<br>Enable a chance of<br>randomly inflicting the <span style=\'color:#880000\'>Stun</span>, <span style=\'color:#880000\'>Blind</span>, <span style=\'color:purple\'><b>Poison</b></span><br>or <span style=\'color:#880000\'>Frozen</span> statuses with<br>each attack.<br>This item cannot be Dropped or Traded.<br>Attack Strength : <span style=\'color:#777777\'>150</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('16428', 'One-handed Sword', '', 'Fire', 'A box that contains:<br><span style=\'color:blue\'>Rental Item</span><br><span style=\'color:#6A5ACD\'>1 Solar Sword</span><br>Duration: 7 Days after opening.<br>A one-handed sword<br>adorned with an engraving of the sun, symbolizing battle spirit and vigor.<br>Regains 1% of the damage inflicted on its target as HP with each attack.<br>Drains 15 SP from its<br>owner every 10 seconds.<br>This item cannot be Dropped or Traded.<br>Attack Strength : <span style=\'color:#777777\'>120</span>', '', '', '');
