REPLACE INTO `re_item_desc_db` VALUES ('17030', 'Headgear', '', '', '<span style=\'color:#ff0000\'>Green Ale\'s effects will decrease in recovery percentage to 10% on April 9, 2013.</span><br>A box containing 1 St Patrick\'s Hat.<br><span style=\'color:#0000ff\'>This hat can also be obtained once per character by participating in the St. Patrick\'s Day event by speaking to the Mysterious Man in Payon</span>.<br><span style=\'color:#ff0000\'>This item is not available for refund or cancellation of purchase once opened.</span><br><span style=\'color:#ff0000\'>This item can only be traded or stored. Once opened, the item inside is account bound.</span><br>Description:<br>This is an exact replica of the hat worn by O\'Riley the Leprechaun.<br>Str +1, Int +1, Mdef +4.<br>If refined to +7 or higher, adds a chance of dropping Green Ales with every monster killed.<br><span style=\'color:#ff0000\'>This item can only be put in storage</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('17053', '', '', '', 'A box that contains 10 of each colored Butterfly Wings and 10 Dungeon Teleport Scrolls.<br><span style=\'color:#ffff00\'>Yellow Butterfly Wing</span>:<br>An enchanted butterfly\'s wing that instantly sends its user to the towns of the Rune Midgarts Kingdom.<br>Warning: If no town is selected, the item will disappear.<br><span style=\'color:#00ff00\'>Green Butterfly Wing</span>:<br>An enchanted butterfly\'s wing that instantly sends its user to the towns of the Schwaltzwalt Republic.<br>Warning: If no town is selected, the item will disappear.<br><span style=\'color:#ff0000\'>Red Butterfly Wing</span>:<br>An enchanted butterfly\'s wing that instantly sends its user to the towns of Arunafeltz.<br>Warning: If no town is selected, the item will disappear.<br><span style=\'color:#0000ff\'>Blue Butterfly Wing</span>:<br>An enchanted butterfly\'s wing that instantly sends its user to the island towns of Rune Midgard.<br>Warning: If no town is selected, the item will disappear.', '', '', '');
