REPLACE INTO `re_item_desc_db` VALUES ('18538', 'Headgear', '', '', 'A spirit literally whispering sweet nothings into your ear and out the other.<br>Unable to refine.<br>Mdef +3<br>Increases resistance to attacks from Demon and Angel monsters by 1%<br>When killing a Demon monster, there\'s a chance of obtaining \'Holy Water\'.<br>When killing an Angel type emonster, there\'s a chance of obtaining \'Cursed Water\'.<br>Special Effect:<br>If equipped in combination with the Heart Hairpin[0], adds an additional 9% resistance to attacks from Angel monsters.<br>If equipped in combination with the Crescent Hairpin[0], adds an additional 9% resistance to attacks from Demon monsters.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('18539', 'Headgear', '', '', 'A driver\'s cap with a menacing skull on the band.<br>Matk +2%<br>If refined to +5 or higher, adds an additional Matk +3%.<br>If refined to +7 or higher, adds an additional Matk +3%.<br>Special Effect:<br>If equipped in combination with Elven Ears[0], decreases the SP cost of skills by 3%.<br>If equipped in combination with Evil Bone Wand[0], each refine on the weapon adds Matk +1%. If the Bone Wand[0] is refined to +10, reduces cast time of skill by 10%.<br>If equipped in combination with Thorn Staff of Darkness[0], each refine on the weapon adds Matk +1%. If the Thorn Staff of Darkness[0] is refined to +10, reduces cast time of skills by 10%.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('18540', 'Headgear', '', '', 'A mask worn by an ancient Rune Midgardian race that worshipped demons.<br>Attack Speed +1%.<br>Drains 1 SP every 2 seconds.<br>Unable to refine.<br>Special Effect:<br>If equipped with Succubus Horn[0] or Incubus Horn[0], Aspd +1, Attack Speed +9%, drains an additional 3 SP every 2 seconds.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('18541', 'Headgear', '', '', 'A soft feathered archer\'s cap that gives you that dash of Errol Flynn flair.<br>Dex +2.<br>Critical Damage +10%.<br>Special Effect:<br>If equipped with \'Sharp Arrow\'s, adds an additional Critical Damage +5%. If equipped with a +7 Little Feather Hat[1] or higher, adds an additional Critical Damage +5% with \'Sharp Arrow\'s.<br>When equipped with the bow +10 Falken Blitz[2], the cast time of [Focus Arrow Strike] is reduced by 50%.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('18542', 'Headgear', '', '', 'A headdress worn by high level members of warrior clergy members.<br>Increases the effect of received healing by 5%.<br>Increases the effectiveness of your own healing skills by 2%.<br>If refined to +7 or higher, increases the effectiveness of your healing skills by an additional 3%.<br>Special Effect:<br>If equipped in combination with a Healing Staff[0], the effectiveness of your healing skills is increased by 1% per refine level of the staff.<br>If equipped in combination with a Holy Stick[1], the effectiveness of your healing skills is increased by 3% per refine level of the staff.', '', '', '');
