REPLACE INTO `re_item_desc_db` VALUES ('18553', 'Headgear', '', '', 'A \'fashionable\' Christmas Tree hat designed by Louise Carol.<br>Increases resistance to Blind, Stun, Curse and Chaos by 20%.<br>Has a 1% chance of dropping Cookie Bags with each monster killed.<br>If refined to +7, Cookie Bag drop rate increases by 2%.<br>If refined to +9, Cookie Bag drop rate increases by 4%.<br><span style=\'color:#ff0000\'>This item can be traded, stored, and put into a cart</span>', '', '', '');
