REPLACE INTO `re_item_desc_db` VALUES ('19111', '', '', '', '<span style=\'color:#00CC00\'>A Tradable Box that contains 10 Dungeon Teleport Scroll 1s</span><br>A scroll on which<br>many dungeon map<br>coordinates have<br>been recorded.<br>If you tear it up,<br>it can transport you<br>once to Nogg Road,<br>Mjolnir Dead Pit,<br>Umbala Dungeon,<br>Einbroch Mine Dungeon,<br>Payon Dungeon,<br>Toy Dungeon 1f,<br>Izlude Dungeon,<br>Turtle Island Dungeon,<br>Clock Tower B3f,<br>Clock Tower 3f,<br>Glast Heim Underprison,<br>Glast Heim Culvert 2f,<br>Glast Heim,<br>Sphinx Dungeon 4f,<br>Inside Pyramid 4f,<br>Inside Pyramid B2,<br>Prontera Culvert 3f,<br>Amatsu Dungeon 1f (Tatami Maze),<br>Somatology Laboratory 1f,<br>Somatology Laboratory 3f<br>Warning - After using it, unless you choose the destination in 1 minute,<br>it will be useless.', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('19112', '', '', '', 'A box that contains<br>10 Job Battle Manuals.<br><span style=\'color:#FF0000\'>This box is account bound. The items inside can be traded and stored.</span><br><br>This well written,<br>detailed manual<br>explains effective<br>battle methods.<br><span style=\'color:#ff0000\'>This item is account bound</span><br><span style=\'color:blue\'>Job EXP rate increases to<br>150% for 30 minutes.<br>This bonus stacks with VIP and Battle Manuals to give an additional job bonus.</span>', '', '', '');
