REPLACE INTO `re_item_desc_db` VALUES ('19402', 'Headgear', '', '', 'A box that contains 1 Classic Chick Hat<br>Description:<br>A hat adorned with a<br>cute chick that brings<br>feelings of joy to those<span style=\'color:#FFFFFF\'>  </span> that see it hopping.<br>Adds 3% resistance to<br>Demihuman and Animal<br>Class monsters. Cannot<br>be upgraded. Enables<br>Lv. 2 Double Attack.<br>Max HP and SP +50<br>Cannot be Dropped, <br>or Tradable<br>Can be placed in Kafra Storage', '', '', '');
