REPLACE INTO `re_item_desc_db` VALUES ('19405', 'Headgear', '', '', 'A box that contains 1 Classic Mini Propeller Hat<br>Description:<br>This hat makes the<br>wearer feel as if he<br>is floating on air.<br>Agi +2, Dex +1,<br>Dodge +10. This<br>headgear\'s<br>upgrade level affects<br>the percentage of<br>Cast Time reduction.<br>Cannot be Dropped, <br>or Tradable<br>Can be placed in Kafra Storage', '', '', '');
