REPLACE INTO `re_item_desc_db` VALUES ('19411', 'Headgear', '', '', 'A Box that contains 1 Parade Hat<br>Description:<br>A hat by those leading the way to celebrations.<br>Mdef +2,<br>Increases tolerance to Stun by 10%.<br>When being physically attacked, has a chance of auto-casting Lv 5 Angelus or Lv 1 Assumptio on onself.<br>If the user is an Acolyte, Priest , High Priest or Arch Bishop class, will auto-cast Lv 1 Lex Aeterna when using Heal Skill on an enemy.<br><span style=\'color:#ff0000\'>This item can be traded, stored, and put into a cart</span>', '', '', '');
