REPLACE INTO `re_item_desc_db` VALUES ('19441', 'Headgear', '', '', 'A box that contains 1 Dragon Skull<br>Description:<br>A helmet made from<br>a dragon\'s skull that<br>provides protection<br>against dragons.<br>Decreases damage<br>from Dragon Class<br>monsters by 5%.<br>Cannot be Dropped, <br>or Tradable<br>Can be placed in Kafra Storage', '', '', '');
