REPLACE INTO `re_item_desc_db` VALUES ('25028', 'Headgear', '', '', '<span style=\'color:#ff0000\'>This unbound item can be traded and sold to npc. Once opened the item inside is account bound.</span><br>A woolen hunting<br>cap once worn by<br>Ulle, god of hunting.<br>Just wearing it makes<br>you feel like you can<br>hunt down any beast.<br>Dex +2, Agi +1.<br><span style=\'color:#6A5ACD\'>Ulle\'s Armor Set</span><br><span style=\'color:#3CB371\'>Ulle\'s Cap</span><br><span style=\'color:#3CB371\'>Odin\'s Blessing</span><br>If Base Dex is 70 or higher:<br>-10% SP consumption', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('25029', 'Headgear', '', '', '<span style=\'color:#ff0000\'>This unbound item can be traded and sold to npc. Once opened the item inside is account bound.</span><br>A strong diadem which has been covered by the face of Satan Morocc. It can be possessed only by the ones who have overcome the fear of Satan Morocc.<br>Adds 20% tolerance to <span style=\'color:#FF0000\'>Stone</span>, <span style=\'color:#FF0000\'>Freeze</span>, and <span style=\'color:#FF0000\'>Stun</span>.<br>Each upgrade level increases tolerance by 2%<br>(but, tolernace increase applies up to refeine level +10)', '', '', '');
