REPLACE INTO `re_item_desc_db` VALUES ('27009', 'Spear', '', '', 'An auger shaped<br>spear that is designed<br>for striking the weak<br>points of an enemy.<br>Ignores physical DEF,<br>and has a certain<br>chance of auto casting<br>Lv.5 Pierce. Inflicts<br>10% additional damage<br>to Demihuman enemies.<br>ATK: <span style=\'color:#777777\'>120</span><br>Weapon LV: <span style=\'color:#777777\'>4</span>', '', '', '');
