REPLACE INTO `re_item_desc_db` VALUES ('27011', 'Katar^000000   Attack Strength : ^777777148', '', '', 'A set of daggers in which the blades are positioned above the knuckles for use in close range combat.<br>Dex + 1', '', '', '');
