REPLACE INTO `re_item_desc_db` VALUES ('27013', 'Katar', '', 'Neutral', 'A lethal Katar that<br>has been designed for<br>quick assassinations.<br>Inflicts 50% more damage<span style=\'color:#FFFFFF\'>  </span> to Demihuman targets.<br>Perfect Dodge +2<br>Dodge Rate +5<br>DEF +3<br>Attack Strength : <span style=\'color:#777777\'>140</span>', '', '', '');
