REPLACE INTO `re_item_desc_db` VALUES ('27015', 'Katar', '', '', 'A Katar that can<br>attack at longer<br>ranges with fearsome<br>power. Has a certain<br>chance of inflicting<br>the Bleeding status<br>to all enemies in a<br>5*5 cell area. This<br>range increases to<br>a 11*11 cell area<br>if this weapon is<br>refined to Upgrade<br>Level 9 or greater.<br>ATK: <span style=\'color:#777777\'>120</span><br>Weapon LV: <span style=\'color:#777777\'>4</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('27016', 'Katar', '', '', 'Although this katar has  an increased number of<br>slots, this weapon is hard to handle and is known to<br>cause injury to those who wield it. Has a low chance<br>of causing the Bleeding effect on its user.<br>Attack Strength : <span style=\'color:#777777\'>90</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('27017', 'Axe', '', '', 'A sturdy, two-handed<br>axe created for fighting multiple enemies at once.<br>Attack Strength : <span style=\'color:#777777\'>80</span>', '', '', '');
