REPLACE INTO `re_item_desc_db` VALUES ('27020', 'Axe', '', '', 'A special Two-Handed<br>Axe that was crafted<br>by a great blacksmith;<br>it seems capable of<br>destroying any kind<br>of armor. CRI +20,<br>increase Attack Speed.<br>delay after attacking reduces by 5%<br>CRI bonus raises<br>as weapon\'s Upgrade<br>Level is increased.<br>Has a chance of<br>auto casting Lv.3<br>Hammerfall when<br>worn by Mastersmiths<br>and Biochemists.<br>ATK: <span style=\'color:#777777\'>175</span><br>Weapon LV: <span style=\'color:#777777\'>4</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('27021', 'Axe', '', '', 'A massive Two-Handed<br>Axe that is embedded<br>with an orb imbued<br>with the power of<br>a hurricane. Reduces<br>damage from Mid size<br>monsters by 10%;<br>raising this weapon\'s<br>Upgrade Level will<br>increase the amount<br>by which this damage<br>is reduced and add<br>an Attack Speed bonus.<br>Each attack has a<br>chance of inflicting<br>a fixed amount of<br>damage to all enemies<br>within a 15*15 cell<br>area, and knock them<br>7 cells backward.	<br>ATK: <span style=\'color:#777777\'>332</span><br>Weapon LV: <span style=\'color:#777777\'>4</span>', '', '', '');
REPLACE INTO `re_item_desc_db` VALUES ('27022', '', '', '', 'Rumored to have<br>been crafted by an<br>ancient wizard, this<br>staff contains awesome<br>energies.<br>Int +6<br>Dex +2<br>Matk +15%<br><span style=\'color:#777777\'>Two-Handed Staff</span><br>Attack Strength : <span style=\'color:#777777\'>120</span>', '', '', '');
